    return (
        <div className="gap-2 m-6">
            <div className="flex my-2">
                <h1 className="text-2xl font-semibold m-2">Filter</h1>
                <select className="m-2 p-2 rounded-lg shadow-lg bg-slate-500 text-white">
                        <option value="all">All</option>
                        <option value="popular">Popular</option>
                        <option value="decrease">Decreasing Price</option>
                        <option value="increase">Increasing Price</option>
                  </select>
            </div>
            <div className="grid grid-cols-4 gap-3">
                {[
                    Array.from(Array(16)).map((_, index) => (
                        <div
                            key={index}
                            className="p-4 rounded-lg shadow-lg bg-slate-500 min-h-48"
                        ><div className="h-48 bg-gray-200 mb-2 rounded-lg shadow-lg">
                        {/* Phần hình ảnh */}
                        <img src={`https://via.placeholder.com/300`} alt={`Product ${index + 1}`} className="object-cover w-full h-full rounded-lg" />
                    </div>
                    <div className="bg-white p-4 rounded-lg shadow-lg">
                        {/* Phần thông tin giày */}
                        <h3 className="text-lg font-semibold mb-2">Product {index + 1}</h3>
                        <p className="text-gray-600">Thông tin về giày...</p>
                    </div>

                            Product {index + 1}
                        </div>
                    )),
                ]}
            </div>
        </div>
    )
}

export default Store
